		var $listingTile = $(this).parents('.listing-tile');
		var propertyId = $listingTile.data('property-id');

 	    var productData = _.findWhere(eCommerceData.products, {
	     	 id: parseInt(propertyId)
	   	});

 	    // if product data defined...
 	    if (productData) {	
